<script>

var htmlSnippet = String.raw`${input.customFields.html_snippet}`;
window.Upscale.payments.global.renderHtml('my-checkout-container',
    new DOMParser().parseFromString(htmlSnippet, "text/html").documentElement.textContent);
var orderId = '${input.orderId}';
var referenceId = '${input.referenceId}';

var getExtTransaction = async function() {
    return await Upscale.payments.global.getExtTransaction(referenceId);
}

var patchOrder = async function(externalTransaction) {
    var upscaleUpdateOrder = {
        billingAddress: {
            addressLine1: externalTransaction.billingAddress.addressLine1 || null,
            addressLine2: externalTransaction.billingAddress.addressLine2 || null,
            city: externalTransaction.billingAddress.city || null,
            zip: externalTransaction.billingAddress.postalCode || null,
            country: externalTransaction.billingAddress.country.toUpperCase() || null,
            emailAddress: externalTransaction.billingAddress.emailAddress || null,
            firstName: externalTransaction.billingAddress.firstName || null,
            lastName: externalTransaction.billingAddress.lastName || null,
            state: externalTransaction.billingAddress.state || null,
            phoneNumber: externalTransaction.billingAddress.phoneNumber || null,
        },
    };
	return await Upscale.payments.global.patchOrder(orderId, upscaleUpdateOrder);
}

var calculateCost = async function(externalTransaction) {
    var calculateCostForOrder = {
        shippingAddress: {
            addressLine1: externalTransaction.shippingAddress.addressLine1 || null,
            addressLine2: externalTransaction.shippingAddress.addressLine2 || null,
            city: externalTransaction.shippingAddress.city || null,
            zip: externalTransaction.shippingAddress.postalCode || null,
            country: externalTransaction.shippingAddress.country.toUpperCase() || null,
            emailAddress: externalTransaction.shippingAddress.emailAddress || null,
            firstName: externalTransaction.shippingAddress.firstName || null,
            lastName: externalTransaction.shippingAddress.lastName || null,
            state: externalTransaction.shippingAddress.state || null,
            phoneNumber: externalTransaction.shippingAddress.phoneNumber || null,
        },
    };

    if (externalTransaction?.selectedShippingOption?.id) {
        calculateCostForOrder.shippingMethod = {
            id: externalTransaction?.selectedShippingOption?.id || null,
        }
    }
    return await Upscale.payments.global.calculateCost(orderId,calculateCostForOrder);
}

var syncOrder = async function(data, callback) {
	try {
        var externalTransaction = await getExtTransaction();
        await calculateCost(externalTransaction);
        await patchOrder(externalTransaction);
        callback({ should_proceed: true });
	} catch (error) {
        console.error("syncOrder process failed.", error);
        callback({ should_proceed: false });
    }
}

window._klarnaCheckout(function(api) {
    api.on({
        'shipping_address_change': async function(data) {
			try {
            var externalTransaction = await getExtTransaction();
            console.log("shipping address change get externalTransaction ", externalTransaction);

            var calculateCostResponse = await calculateCost(externalTransaction);
            console.log('hipping address change calculate cost for order response', calculateCostResponse);

            //refresh amount and selected shipping option to klarna
            var shippingOptions = [];
            for (var i = 0; i < calculateCostResponse.shippingMethods.length; i++) {
                shippingOptions.push({
                    id: calculateCostResponse.shippingMethods[i].id,
                    name: calculateCostResponse.shippingMethods[i].name,
                    defaultFlag: calculateCostResponse.shippingMethods[i].defaultFlag,
                    cost: calculateCostResponse.shippingMethods[i].cost,
                    shFeeTax: calculateCostResponse.shippingMethods[i].shFeeTax,
                    shFeeTaxPercent: calculateCostResponse.shippingMethods[i].shFeeTaxPercent,
                    shFeeWithTax: calculateCostResponse.shippingMethods[i].shFeeWithTax
                });
            };

            window._klarnaCheckout(function (api) {
                api.suspend();
            });
            var transactionUpdate = await Upscale.payments.global.updateExtTransaction(orderId, referenceId, shippingOptions)
            console.log('shipping address change update transaction success', transactionUpdate);
		    } catch (error) {
              console.error("shipping_address_change process failed.", error);
            }
            window._klarnaCheckout(function (api) {
                api.resume();
            });
        },
	    'validation_callback': syncOrder
    });
});
